mapper.select:
SELECT t.status as talent_status, t.title, t.sale_bamboo, TO_CHAR(t.reg_date,'yy-mm-dd') as REG_DATE, (SELECT count(*) FROM bamboo_use u WHERE u.talent_no = t.talent_no) AS sell_Count, TO_CHAR(u.purchase_date,'yy-mm-dd') as PURCHASE_DATE, TRUNC(sysdate) - TRUNC(u.purchase_date) AS REFUNDABLE_DATE, r.status AS refund_status, TO_CHAR(r.submit_date,'yy-mm-dd') as REFUND_SUBMIT_DATE, TO_CHAR(r.approve_date,'yy-mm-dd') as REFUND_APPROVE_DATE, u.buyer_id, t.seller_id, t.talent_no, r.purchase_no as refund_purchase_no, e.status as exchange_status, u.use_bamboo FROM talent t LEFT JOIN bamboo_use u ON t.talent_no = u.talent_no LEFT JOIN talent_refund r ON u.purchase_no = r.purchase_no LEFT JOIN exchange e on e.talent_no = t.talent_no
WHERE u.buyer_id = #{memberId} AND r.status IS NULL
WHERE u.buyer_id = #{memberId} AND r.status IS NOT NULL
WHERE t.seller_id = #{memberId}
WHERE u.buyer_id = #{memberId} AND r.status IS NULL OR u.buyer_id = #{memberId} AND r.status IS NOT NULL OR t.seller_id = #{memberId}
ORDER BY reg_date DESC

mapper.update:
UPDATE talent
SET status = '판매종료'
WHERE talent_no = #{talentNo}
