mapper.select:
SELECT member_id
FROM member
WHERE name = #{name} AND email = #{email}

SELECT password
FROM member
WHERE member_id = #{memberId} AND email = #{email}

mapper.update:
 	UPDATE member
	SET password = #{newPw}
